import React, { Component } from "react";

// form
import { FaPlus } from 'react-icons/fa'

// task
import { FaEdit, FaWindowClose } from 'react-icons/fa'

import './Main.css'

export default class Main extends Component {
  state = {
    newTask: '',
    tasks: []
  };

  hundleInput = (e) => {
    this.setState({
      newTask: e.target.value
    })

  }

  hundleSubmit = (e) => {
    e.preventDefault()
    const { task } = this.state

    let { newTask } = this.state
  }

  render() {
    const { newTask, tasks } = this.state;

    return (
      <div className="main">
        <h1>Lista de tarefas</h1>
        <p>{newTask}</p>

        <form className="form" action="#" onSubmit={this.hundleSubmit}>
          <input onChange={this.hundleInput} type="text"></input>
          <button type="submit">
            <FaPlus/>
          </button>
        </form>

        <ul className="tasks">
            {tasks.map(task => (
                <li key={task}>
                   {task}
                    <span>
                        <FaEdit className="edit"/>
                        <FaWindowClose className="delete"/>
                    </span>
                </li>
            ))}
        </ul>
      </div>
    );
  }
}
