import React, { Component } from "react";

// form
import { FaPlus } from 'react-icons/fa'

// task
import { FaEdit, FaWindowClose } from 'react-icons/fa'

import './Main.css'

export default class Main extends Component {
  state = {
    newTask: '',
    tasks: [],
    errors: []
  };

  hundleInput = (e) => {
    this.setState({
      newTask: e.target.value
    })

  }

  hundleSubmit = (e) => {
    e.preventDefault()
    const { tasks } = this.state
    let { newTask } = this.state
    let { errors } = this.state
    let error = [...errors]
    newTask = newTask.trim()

    if (newTask === '') {
        error.push('Tarefa não pode esta vazia')
    }

    if(tasks.indexOf(newTask) !== -1) {
        error.push('Tarefa já cadastrada')
    }

    this.setState({
        errors: error
    })

    if (error.length > 0) {
        return
    }

    const newsTasks = [...tasks, newTask];

    this.setState({
        tasks: newsTasks
    })
  }

  render() {
    const { newTask, tasks, errors } = this.state;

    return (
      <div className="main">
        <h1>Lista de tarefas</h1>
        <p>{newTask}</p>

        <form className="form" action="#" onSubmit={this.hundleSubmit}>
          <input onChange={this.hundleInput} type="text"></input>
          <button type="submit">
            <FaPlus/>
          </button>
        </form>

        <ul className="tasks">

            {errors.length > 0 ? (
                <div>
                    {errors.map(error => (
                        <p key={error}>
                            {error}
                        </p>
                    ))}
                </div>
            ) : (<div></div>)}

            {tasks.map(task => (
                <li key={task}>
                   {task}
                    <span>
                        <FaEdit className="edit"/>
                        <FaWindowClose className="delete"/>
                    </span>
                </li>
            ))}
        </ul>
      </div>
    );
  }
}
