import React, { Component } from "react";

// form
import { FaPlus } from 'react-icons/fa'

// task
import { FaEdit, FaWindowClose } from 'react-icons/fa'

import './Main.css'

export default class Main extends Component {
  state = {
    newTask: '',
    tasks: [],
    errors: [],
    index: -1
  };

  componentDidMount() {

  }

  hundleInput = (e) => {
    this.setState({
      newTask: e.target.value
    })

  }

  hundleSubmit = (e) => {
    e.preventDefault()
    const { tasks, index} = this.state
    let { newTask } = this.state
    newTask = newTask.trim()
    const newTasks = [...tasks]

    if (newTask === '') {
        return
    }

    if(tasks.indexOf(newTask) !== -1) {
        return
    }

    if(index === -1) {
        this.setState({
            newTask: '',
            tasks:  [...tasks, newTask]
        })
    } else {
        newTasks[index] = newTask

        this.setState({
            tasks: [...newTasks],
            index: -1
        })
    }
  }

  handleEdit = (e, index) => {
    const { tasks } = this.state

    this.setState({
        index,
        newTask: tasks[index],
    })
  }

  handleDelete = (e, index) => {
    const { tasks } = this.state

    const newTasks = [...tasks]
    newTasks.splice(index, 1)

    this.setState({
        tasks: newTasks
    })
  }


  render() {
    const {newTask, tasks } = this.state;

    return (
      <div className="main">
        <h1>Lista de tarefas</h1>

        <form className="form" action="#" onSubmit={this.hundleSubmit}>
          <input
            onChange={this.hundleInput}
            type="text"
            value={newTask}
          ></input>
          <button type="submit">
            <FaPlus/>
          </button>
        </form>

        <ul className="tasks">

            {tasks.map((task, index) => (
                <li key={task}>
                   {task}
                    <span>
                        <FaEdit
                            className="edit"
                            onClick={(e) => {this.handleEdit(e, index)}}
                        />

                        <FaWindowClose
                            className="delete"
                            onClick={(e) => { this.handleDelete(e, index)}}
                        />
                    </span>
                </li>
            ))}
        </ul>
      </div>
    );
  }
}
